import React, { useState } from "react";
// import api from "../api/axios";

export default function Bots() {
  const [bots, setBots] = useState([
    { id: 1, name: "FAQ Bot", description: "Answers common queries" },
    { id: 2, name: "Sales Bot", description: "Qualifies leads" }
  ]);

  // Example create handler
  const createBot = async () => {
    // const res = await api.post("/bots/", { name: "New Bot", description: "" });
    // setBots([...bots, res.data]);
    setBots([...bots, { id: Date.now(), name: "New Bot", description: "Demo created" }]);
  };

  return (
    <div>
      <div className="flex justify-between items-center mb-4">
        <h2 className="text-2xl font-extrabold text-primary">Your Bots</h2>
        <button onClick={createBot} className="px-4 py-2 rounded-xl bg-primary text-white hover:bg-secondary transition">New Bot</button>
      </div>
      <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-4">
        {bots.map((b) => (
          <div key={b.id} className="bg-white rounded-2xl shadow-soft p-4">
            <div className="font-semibold">{b.name}</div>
            <div className="text-sm text-gray-500">{b.description}</div>
            <button className="mt-3 px-3 py-2 rounded-lg bg-white border hover:bg-light">Configure</button>
          </div>
        ))}
      </div>
    </div>
  );
}
